    public static void miniMap(int dir)
    {
        final String INDENT = "                                     ";
        int cast = 8594;
        for (int i = 0; i < dir; i++)
        {
            cast--;
            if (cast == 8591)
            {
                cast = 8595;
            }
        }
        char arrow = (char) cast;
        System.out.println(Font.format("bold-default", INDENT + "    NORTH\n" + INDENT + "EAST  " + arrow + "  WEST\n" + INDENT + "    SOUTH"));
    }
    
